const input = $input.first();
let text = '';
if (typeof input.json === 'string') text = input.json;
else if (input.json.data) text = input.json.data;
else if (input.binary) text = Buffer.from(input.binary.data.data, 'base64').toString('utf-8');

const lines = text.split('\n').filter(l => l.trim());
const headers = lines[0].split(',').map(h => h.trim());

const result = [];
for (let i = 1; i < lines.length; i++) {
  const line = lines[i];
  if (!line.trim()) continue;
  const values = [];
  let current = '';
  let inQuotes = false;
  for (let char of line) {
    if (char === '"') inQuotes = !inQuotes;
    else if (char === ',' && !inQuotes) {
      values.push(current.trim());
      current = '';
    } else current += char;
  }
  values.push(current.trim());
  const obj = {};
  headers.forEach((h, idx) => obj[h] = values[idx] || '');
  result.push({ json: obj });
}
return result;

const input = $input.first();
let text = '';
if (typeof input.json === 'string') text = input.json;
else if (input.json.data) text = input.json.data;
else if (input.binary) text = Buffer.from(input.binary.data.data, 'base64').toString('utf-8');

const lines = text.split('\n').filter(l => l.trim());
const headers = lines[0].split(',').map(h => h.trim());

const result = [];
for (let i = 1; i < lines.length; i++) {
  const line = lines[i];
  if (!line.trim()) continue;
  const values = [];
  let current = '';
  let inQuotes = false;
  for (let char of line) {
    if (char === '"') inQuotes = !inQuotes;
    else if (char === ',' && !inQuotes) {
      values.push(current.trim());
      current = '';
    } else current += char;
  }
  values.push(current.trim());
  const obj = {};
  headers.forEach((h, idx) => obj[h] = values[idx] || '');
  result.push({ json: obj });
}
return result;

const newsByDate = {};
for (const item of $input.all()) {
  let d = item.json.date || (item.json.datetime ? item.json.datetime.split(' ')[0] : '');
  if (!d) continue;
  if (!newsByDate[d]) newsByDate[d] = [];
  newsByDate[d].push(item.json.title);
}
return [{ json: { newsByDate } }];

const allItems = $input.all();
let newsLookup = {};
const marketData = [];

for (const item of allItems) {
  if (item.json.newsByDate) newsLookup = item.json.newsByDate;
  else if (item.json.date) marketData.push(item);
}

marketData.sort((a, b) => new Date(a.json.date) - new Date(b.json.date));

const merged = [];
for (const item of marketData) {
  let d = item.json.date.split(' ')[0];
  const news = newsLookup[d] || [];
  const newsText = news.length > 0 ? news.slice(0, 3).join(' | ') : "Market is quiet today.";

  merged.push({
    json: {
      ...item.json,
      news_text: newsText,
      news_count: news.length
    }
  });
}

return merged;

// Simple Batch Preparation
const allItems = $input.all();
const inputsArray = allItems.map(item => item.json.news_text);

// We store original items to merge later
return [{
  json: {
    batch_inputs: inputsArray,
    original_items: allItems.map(i => i.json)
  }
}];

// Enhanced strategy for higher Sharpe ratio
const item = $input.first();

// 1. Get Data
let apiResponse = item.json.data || item.json;
const prepareNode = $("4. Prepare Batch").first();
const originalItems = prepareNode.json.original_items; 

if (!originalItems) throw new Error("Missing original_items");

// 2. Normalize API
let sentiments = apiResponse;
if (Array.isArray(apiResponse) && Array.isArray(apiResponse[0])) sentiments = apiResponse;
else if (!Array.isArray(apiResponse)) sentiments = [apiResponse];

// 3. Setup Portfolio
const tickers = [...new Set(originalItems.map(i => i.ticker))]; 
const initialCapitalPerTicker = 10000 / tickers.length; 
const portfolio = {};

tickers.forEach(t => {
    portfolio[t] = { 
        balance: initialCapitalPerTicker, 
        coin: 0, 
        inPosition: false, 
        entryPrice: 0,
        peakPrice: 0,
        lastEquity: initialCapitalPerTicker,
        // Dynamic stop will be set per entry based on volatility
        dynamicStopPct: 0.05  // default, will be updated on entry
    };
});

// 4. Helper functions
function sigmoid(x) {
    return 1 / (1 + Math.exp(-x));
}

function rsiSignal(rsi) {
    // Use sigmoid to create smooth transition between -1 and 1
    if (rsi < 30) return (30 - rsi) / 30;          // linear
    if (rsi > 70) return -(rsi - 70) / 30;
    return 0;
}

function bbSignal(bb_pos) {
    if (bb_pos < 0.2) return (0.2 - bb_pos) / 0.2;
    if (bb_pos > 0.8) return -(bb_pos - 0.8) / 0.2;
    return 0;
}

function volumeSignal(volumeChange, obv) {
    // Volume spike up positive, spike down negative
    if (volumeChange > 0.5) return 0.3;   // high volume increase
    if (volumeChange < -0.5) return -0.3;
    // Could also use OBV trend, but simple for now
    return 0;
}

function volatilityAdjustment(vol_7d, baseThreshold = 0.3) {
    // Higher volatility → need higher conviction (threshold increases)
    // Lower volatility → easier to trigger trades
    const volFactor = Math.min(2, Math.max(0.5, vol_7d / 0.03)); // assuming 3% avg daily vol
    return baseThreshold * volFactor;
}

// 5. Processing Loop
const trades = [];
let totalTrades = 0;
let winningTrades = 0;
let totalProfit = 0;
let totalLoss = 0;

const loopCount = Math.min(originalItems.length, sentiments.length);

for (let i = 0; i < loopCount; i++) {
    const d = originalItems[i];
    const s = sentiments[i];
    const ticker = d.ticker;
    const price = parseFloat(d.price || d.close);

    const wallet = portfolio[ticker];
    if (!wallet) continue;

    // --- Sentiment aggregation with news count filter ---
    let sentimentNet = 0;
    const newsCount = parseInt(d.news_count) || 0;
    if (Array.isArray(s) && s.length > 0 && newsCount > 0) {
        let sum = 0;
        s.forEach(item => {
            if (item.label === 'POSITIVE') sum += item.score;
            else if (item.label === 'NEGATIVE') sum -= item.score;
        });
        sentimentNet = sum / s.length;
        // Reduce impact if few news articles
        if (newsCount < 5) sentimentNet *= 0.5;
    } else if (s && s.label && newsCount > 0) {
        sentimentNet = s.label === 'POSITIVE' ? s.score : (s.label === 'NEGATIVE' ? -s.score : 0);
        if (newsCount < 5) sentimentNet *= 0.5;
    }

    // --- Technical signals ---
    const rsi = parseFloat(d.rsi) || 50;
    const rsiSig = rsiSignal(rsi);

    const macdHist = parseFloat(d.macd_hist) || 0;
    // Use sign and magnitude, but cap at 0.5
    const macdSig = Math.min(0.5, Math.max(-0.5, macdHist * 10)); // scale to reasonable range

    const bbPos = parseFloat(d.bb_position) || 0.5;
    const bbSig = bbSignal(bbPos);

    const ma7 = parseFloat(d.ma7);
    const ma20 = parseFloat(d.ma20);
    const trendSig = (ma7 && ma20) ? (ma7 > ma20 ? 1 : -1) : 0;

    const volumeChange = parseFloat(d.volume_change) || 0;
    const obv = parseFloat(d.obv) || 0; // not used directly
    const volSig = volumeSignal(volumeChange, obv);

    const vol7d = parseFloat(d.volatility_7d) || 0.03; // default 3%

    // --- Composite score with dynamic weights ---
    // Higher volatility → more weight on technicals, less on sentiment?
    // We'll keep fixed weights but use volatility to adjust threshold.
    const totalScore = 0.25 * rsiSig + 0.20 * macdSig + 0.15 * bbSig + 
                       0.15 * sentimentNet + 0.15 * trendSig + 0.10 * volSig;

    // --- Dynamic threshold based on volatility ---
    const baseThreshold = 0.25; // lower than before to capture more trades? careful
    const threshold = volatilityAdjustment(vol7d, baseThreshold);

    // --- Decision ---
    let decision = 'hold';
    let reason = `Score ${totalScore.toFixed(2)} (th=${threshold.toFixed(2)})`;

    if (!wallet.inPosition && totalScore > threshold) {
        decision = 'buy';
        reason = 'Composite BUY signal';
    } else if (wallet.inPosition && totalScore < -threshold) {
        decision = 'sell';
        reason = 'Composite SELL signal';
    }

    // --- Trailing stop management (dynamic based on volatility) ---
    if (wallet.inPosition) {
        // Update peak price
        if (!wallet.peakPrice || wallet.peakPrice === 0) wallet.peakPrice = wallet.entryPrice;
        wallet.peakPrice = Math.max(wallet.peakPrice, price);

        // Set stop percentage based on entry volatility
        const entryVol = parseFloat(d.volatility_7d) || 0.03;
        // More volatile -> wider stop to avoid whipsaw, but still cap at 8%
        const stopPct = Math.min(0.08, Math.max(0.02, entryVol * 2));
        if (price < wallet.peakPrice * (1 - stopPct)) {
            decision = 'sell';
            reason = `Trailing stop (${(stopPct*100).toFixed(1)}%)`;
        }
    }

    // --- Execution ---
    let action = 'none';
    let pnl = 0;

    if (decision === 'buy' && !wallet.inPosition) {
        wallet.entryPrice = price;
        wallet.peakPrice = price;
        // Determine position size based on volatility? Not implemented here to keep simple.
        wallet.coin = wallet.balance / price;
        wallet.balance = 0;
        wallet.inPosition = true;
        action = 'buy';

    } else if (decision === 'sell' && wallet.inPosition) {
        const sellValue = wallet.coin * price;
        pnl = sellValue - (wallet.coin * wallet.entryPrice);

        if (pnl > 0) {
            winningTrades++;
            totalProfit += pnl;
        } else {
            totalLoss += Math.abs(pnl);
        }

        wallet.balance = sellValue;
        wallet.coin = 0;
        wallet.inPosition = false;
        wallet.peakPrice = 0;
        totalTrades++;
        action = 'sell';
    }

    // --- Mark‑to‑market equity ---
    const currentTickerEquity = wallet.inPosition ? (wallet.coin * price) : wallet.balance;
    wallet.lastEquity = currentTickerEquity;

    // --- Log ---
    trades.push({
        date: d.date,
        ticker: ticker,
        price: price,
        decision: decision,
        action: action,
        pnl: pnl !== 0 ? pnl.toFixed(2) : '',
        rsi: rsi.toFixed(1),
        sentiment_label: sentimentNet > 0.1 ? 'POSITIVE' : (sentimentNet < -0.1 ? 'NEGATIVE' : 'NEUTRAL'),
        sentiment_score: sentimentNet.toFixed(2),
        equity: currentTickerEquity.toFixed(2),
        reason: reason
    });
}

// 5. Calculate Daily Portfolio Equity Curve
// Group trades by date to get the sum of ALL tickers' equity at that date.
// Problem: If a ticker doesn't trade on a date (no data?), we should use its last known equity.
// But assuming dense data (all tickers have data for all days).

const dateEquityMap = {};
const uniqueDates = [...new Set(trades.map(t => t.date))].sort();

// To be precise: For each date, sum the equity of all tickers on that date.
// The 'trades' array contains equity snapshot for that ticker on that date.
trades.forEach(t => {
    if (!dateEquityMap[t.date]) dateEquityMap[t.date] = 0;
    dateEquityMap[t.date] += parseFloat(t.equity);
});

const equityCurve = uniqueDates.map(d => dateEquityMap[d]);
// Add initial balance at start for calculation?
// Better to just use daily closes.

// 6. Calculate Metrics (Sharpe & MDD)
let peak = -Infinity;
let maxDrawdown = 0;
const dailyReturns = [];

// MDD on Portfolio Curve
for (const eq of equityCurve) {
    if (eq > peak) peak = eq;
    const dd = (peak - eq) / peak;
    if (dd > maxDrawdown) maxDrawdown = dd;
}

// Sharpe Ratio
for (let i = 1; i < equityCurve.length; i++) {
    const prev = equityCurve[i-1];
    const curr = equityCurve[i];
    const ret = prev !== 0 ? (curr - prev) / prev : 0;
    dailyReturns.push(ret);
}

const avgDailyRet = dailyReturns.reduce((a, b) => a + b, 0) / dailyReturns.length || 0;
const variance = dailyReturns.reduce((a, b) => a + Math.pow(b - avgDailyRet, 2), 0) / dailyReturns.length || 0;
const stdDev = Math.sqrt(variance);
const sharpeRatio = stdDev !== 0 ? (avgDailyRet / stdDev) * Math.sqrt(365) : 0;

// Final Stats
const finalTotalEquity = equityCurve[equityCurve.length - 1] || 10000;
const totalReturn = ((finalTotalEquity - 10000) / 10000) * 100;
const winRate = totalTrades > 0 ? ((winningTrades / totalTrades) * 100) : 0;
const profitFactor = totalLoss > 0 ? (totalProfit / totalLoss) : (totalProfit > 0 ? 999 : 0);

return [{
  json: {
    metric_summary: {
      "Total Trades": totalTrades,
      "Win Rate": winRate.toFixed(2) + "%",
      "Profit Factor": profitFactor.toFixed(2),
      "Total Return": totalReturn.toFixed(2) + "%",
      "Sharpe Ratio": sharpeRatio.toFixed(2),
      "Max Drawdown": "-" + (maxDrawdown * 100).toFixed(2) + "%",
      "Final Balance": "$" + finalTotalEquity.toFixed(2)
    },
    trade_log: trades
  }
}];

const input = $input.first();
const trades = input.json.trade_log;
const summary = input.json.metric_summary;

if (!trades) throw new Error("No trade data");

const header = 'date,ticker,price,decision,rsi,sentiment,reason\n';
const csvRows = trades.map(t => {
  const safeReason = (t.reason || '').replace(/"/g, "'");
  return `${t.date},${t.ticker},${t.price},${t.decision},${t.rsi},${t.sentiment_label},"${safeReason}"`;
}).join('\n');

const csvContent = header + csvRows;

return [{
  json: {
    summary: summary,
    message: "CSV Ready"
  },
  binary: {
    data: {
      data: Buffer.from(csvContent).toString('base64'),
      mimeType: 'text/csv',
      fileName: 'final_results.csv'
    }
  }
}];
